mapper.update:
UPDATE POST_INFO
SET POST_TITLE = #{ postTitle },
    POST_CONTENT = #{ postContent }
WHERE POST_CODE = #{ postCode }

mapper.insert:
INSERT
INTO POST_REPLY   (
                  POST_REPLY_CONTENT
                 , REF_POST_REPLY_WRITER
                 , REF_POST_CODE
                 )
VALUES  (
          #{replyCon}
        , #{refReplyWriter}
        , #{refPostCode}
        )

INSERT
INTO POST_INFO      (
                      POST_TYPE
                    , POST_SORT
                    , POST_TITLE
                    , POST_CONTENT
                    , REF_POST_WRITER
                    )
VALUES  (
          #{postType}
        , #{postSort}
        , #{postTitle}
        , #{postContent}
        , #{refPostWriter.memCode}
        )

    SELECT LAST_INSERT_ID()

INSERT
INTO ATTACHMENT_FILE    (
                          REF_POST_CODE
                        , ATTACH_ORIGIN_NAME
                        , ATTACH_DB_NAME
                        , ATTACH_ORIGIN_ADDR
                        , ATTACH_THUMB_ADDR
)
VALUES  (
            #{refPostCode}
        , #{attachOriginName}
        , #{attachDbName}
        , #{attachOriginAddr}
        , #{attachThumbAddr}
        )
