mapper.insert:
INSERT
    INTO MEM_INFO       (
                         MEM_ID
                         ,MEM_PWD
                         ,MEM_NAME
                         ,MEM_BIRTH
                         ,MEM_EMAIL
                         ,MEM_ADRS
                         ,MEM_ADRS_DETAIL
                         )
VALUES (
        #{ memId }
        ,#{ memPwd}
        ,#{ memName }
        ,#{ memBirth }
        ,{ memEmail }
        ,#{ memAdres }
        ,#{ memAdresDetail }
       )

mapper.update:
UPDATE
    MEM_INFO
