mapper.insert:
INSERT INTO DOG_GUM_USE (
                        DOG_GUM_USE_DATE
                        ,DOG_GUM_USE_AMOUNT
                        ,REF_MEM_CODE
                        ,REF_POST_CODE
                        )
VALUES (
        #{ dogGumUseDate }
        ,#{ dogGumUseAmount }
        ,#{ refMemCode }
,#{ refPostCode, jdbcType=INTEGER }
        )

mapper.select:
SELECT
        COUNT(*) > 0
FROM
        DOG_GUM_USE
WHERE
        REF_MEM_CODE = #{ refMemCode }
AND
        DOG_GUM_USE_DATE = #{ dogGumUseDate }
AND
        REF_POST_CODE = #{ refPostCode }

SELECT
        COUNT(*)
FROM
        DOG_GUM_USE
WHERE
        REF_MEM_CODE = #{ refMemCode }
AND
        DOG_GUM_USE_DATE = #{ dogGumUseDate }
AND
        REF_POST_CODE = #{ refPostCode }
